mapper.insert:
INSERT
INTO prods(prod_nm, prod_pri, prod_ent, prod_dis, prod_pic, mt_no)
VALUES (#{name}, #{price}, #{entity}, #{discountRate}, #{photo}, #{mart.no});

UPDATE prods
SET prod_nm=#{name}, prod_pri=#{price}, prod_ent=#{entity}, prod_dis=#{discountRate}, mt_no=#{mart.no} 

, prod_pic=#{photo}

WHERE prod_no=#{no}

mapper.select:
SELECT prod_no, prod_nm, prod_pri, prod_ent, prod_dis, prod_pic, m.mt_no
FROM prods p, marts m
WHERE  p.mt_no = m.mt_no AND m.mt_nm = #{martName}
order by prod_nm asc
limit #{startIndex}, #{len}

SELECT COUNT(prod_no)
FROM prods p, marts m
WHERE  p.mt_no = m.mt_no AND m.mt_nm = #{value}
